    {{ form_label(form.title, 'Titre') }}
    {{ form_errors(form.title) }}
    {{ form_widget(form.title, {'attr': {'class': 'input'}}) }}

    {{ form_label(form.content, 'Contenu') }}
    {{ form_errors(form.content) }}
    {{ form_widget(form.content, {'attr': {'class': 'input'}}) }}


